mapper.select:
select count(*)
from BoardLike
where fk_id = #{fk_id}
  and fk_boardNo = #{fk_boardNo}

mapper.insert:
       insert into BoardLike (fk_id, fk_boardNo)
       values (
			#{fk_id, jdbcType=VARCHAR},
           #{fk_boardNo, jdbcType=NUMERIC}    
            )

mapper.delete:
delete from BoardLike
where fk_id = #{fk_id, jdbcType=VARCHAR}
  and fk_boardNo = #{fk_boardNo, jdbcType=NUMERIC}

