mapper.select:
select count(*)
from commentDislike cd
join comments c on cd.fk_commentNo = c.commentNo
where cd.fk_id = #{fk_id}
  and cd.fk_commentNo = #{commentNo}
  and c.parentNo is null

mapper.insert:
       insert into commentDislike (fk_id, fk_commentNo)
       values (
			#{fk_id, jdbcType=VARCHAR},
           #{commentNo, jdbcType=NUMERIC}    
            )

mapper.delete:
delete from commentDislike
where fk_id = #{fk_id, jdbcType=VARCHAR}
  and fk_commentNo = #{commentNo, jdbcType=NUMERIC}

mapper.select:
select count(*)
from commentDislike cd
join comments c on cd.fk_commentNo = c.commentNo
where cd.fk_id = #{fk_id}
  and cd.fk_commentNo = #{commentNo}
 and c.parentNo is not null

mapper.insert:
       insert into commentDislike (fk_id, fk_commentNo)
       values (
			#{fk_id, jdbcType=VARCHAR},
           #{commentNo, jdbcType=NUMERIC}    
            )

mapper.delete:
delete from commentDislike
where fk_id = #{fk_id, jdbcType=VARCHAR}
  and fk_commentNo = #{commentNo, jdbcType=NUMERIC}
