from commentLike cl
join comments c on cl.fk_commentNo = c.commentNo
where cl.fk_id = #{fk_id}
  and cl.fk_commentNo = #{commentNo}
  and c.parentNo is not null

mapper.insert:
       insert into commentLike (fk_id, fk_commentNo)
       values (
			#{fk_id, jdbcType=VARCHAR},
           #{commentNo, jdbcType=NUMERIC}    
            )

mapper.delete:
delete from commentLike
where fk_id = #{fk_id, jdbcType=VARCHAR}
  and fk_commentNo = #{commentNo, jdbcType=NUMERIC}
